mapper.select:
select PROP_NO as NO, EMP_NAME, PROP_NAME, PROP_CODE as CODE, PROP_DATE, PROP_REMARK as REMARK, PROP_BUY_DATE as BUY_DATE, PROP_FLAG as FLAG from ( select PROP.PROP_NO, EMP.EMP_NAME, PROP.PROP_NAME, PROP.PROP_CODE, PROP.PROP_DATE, PROP.PROP_REMARK, PROP.PROP_BUY_DATE, PROP.PROP_FLAG, row_number() over (order by PROP_NO desc) rn from BI_PROP PROP, BI_EMP EMP where EMP.EMP_ID(+) = PROP.EMP_ID
and EMP.EMP_NAME like '%' || #{EMP_ID} || '%'
and PROP.PROP_NO like '%' || #{PROP_NO} || '%'
and PROP.PROP_NAME like '%' || #{PROP_NAME} || '%'
and PROP.PROP_FLAG = #{PROP_FLAG}
) where rn >= #{beginIndex} and rn <= #{endIndex} order by PROP_NO desc
